def get_numberofbuys(contract_address, block):
    

    url = "https://eth-mainnet.g.alchemy.com/"
    transaction_hashes = []
    payload = {
        "id": 1,
        "jsonrpc": "2.0",
        "method": "alchemy_getAssetTransfers",
        "params": [
            {
                "fromBlock": block,
                "toBlock": block,
                "withMetadata": False,
                "excludeZeroValue": True,
                "maxCount": "0x3e8",
                "contractAddresses": [contract_address],
                "category": ["erc20"],
                "order": "desc"
            }
        ]
    }
    headers = {
        "accept": "application/json",
        "content-type": "application/json"
    }

    response = requests.post(url, json=payload, headers=headers)

    #print(response.text)

    response = requests.post(url, json=payload, headers=headers)
    response_dict = json.loads(response.text)
    #print(type(response_dict))

    for elem in response_dict['result']['transfers']:
        if elem['hash'] not in transaction_hashes:
            transaction_hashes.append(elem['hash'])
    
    print("Number of buys: ", len(transaction_hashes))

    return len(transaction_hashes)

