async def get_event(ws_url):
    last_string = ''
    last_address = ''
    while True:
        try:
            counter = 0
            async with websockets.connect(ws_url) as ws:
                # Send a subscription request for the Transfer event logs
                await ws.send(json.dumps({
                    "id": 1,
                    "method": "eth_subscribe",
                    "params": [
                        "newHeads"
                    ],
                    "jsonrpc": "2.0"
                }))

                # Wait for the subscription response and print it
                subscription_response = await ws.recv()
                print(f'Subscription response: {subscription_response}')
                sendcustom_message('Connection established. Start crawler')
                live = True
                while live:
                    try:
                        log = await asyncio.wait_for(ws.recv(), timeout=18)      
                        log_data = json.loads(log)
                        new_block_number_hex = log_data['params']['result']['number']
                        new_block_number =  int(log_data['params']['result']['number'], 16)  
                        print(new_block_number)
                        print(type(new_block_number_hex))

                        transactions = w3.eth.get_block(new_block_number,full_transactions=True)['transactions']
                        for x in transactions:
                            my_dict = dict(x)
                            transaction_index = my_dict['transactionIndex']
                            if transaction_index == 0:
                                 counter += 1
                                 print(str(counter))
                                 if counter % 50 == 0:
                                    sendmessage_lastblock(str(new_block_number))
                                    counter = 0  

                            if 'input' in my_dict:
                                transaction_input = my_dict['input'].hex()
                                transaction_nonce = my_dict['nonce']
                                transaction_index = my_dict['transactionIndex']
                                transaction_to = my_dict['to']
                                transaction_blocknumber = my_dict['blockNumber']
                                
                                if transaction_to is None:
                                    continue

                                if ((transaction_index == 0) or (transaction_index == 1)):
                                    


                                    if transaction_to.lower() == '0x7a250d5630b4cf539739df2c5dacb4c659f2488d':
                                        token_address = get_token_address(transaction_input)
                                        transaction_to = token_address

                                        if len(transaction_to) < 1:
                                            continue
                                        

                                        
                                    
                                    name_of_token = gettokenname(transaction_to)
                                    await asyncio.sleep(0.75)
                                    if (len(name_of_token)<1) or (name_of_token in ['usdc', 'tether usdt','tether usd','wrapped ether','usd coin','sweth']):
                                        print("Skipped this address" + str(counter))
                                        continue
                                    await asyncio.sleep(1.25)
                                    number_of_buys = get_numberofbuys(transaction_to , new_block_number_hex)
                                    
                                    print(name_of_token)
                                    print(number_of_buys)

                                    if number_of_buys >= 10:
                                        hyped = False
                                        graph_url, popular = getpopularity(name_of_token)
                                        
                                        if len(last_address) == 0:
                                            last_string = get_longest_word(name_of_token)
                                            last_address = transaction_to
                                        else:
                                            new_word = get_longest_word(name_of_token)
                                            hyped = (new_word.lower() == last_string.lower()) and (transaction_to != last_address) and (len(new_word)>2 )
                                            print("New word ", new_word)
                                            print("Old word ", last_string)
                                            print("Last address ", last_address)
                                            print("New address ", transaction_to)
                                            
                                            last_string = new_word
                                            last_address = transaction_to

                                        if (popular) or (hyped):
                                            sendmessage_totelegram(name_of_token,transaction_to,graph_url)
                                            print("Send Message to telegram")

                                        sendmessage_foundcoin(name_of_token,number_of_buys)
                                    
                            

                            
                    except asyncio.TimeoutError:
                        # Handle timeout exception here
                        print("Timeout occurred. Reconnecting...")
                        await asyncio.sleep(1)
                        sendcustom_message('Timeout occurred')
                        live = False
        except websockets.ConnectionClosed:
            print("Connection closed. Reconnecting...")
            sendcustom_message('Connection lost')
              # Wait for a few seconds before attempting to reconnect
        except Exception as e:
            exception_message = f"Caught an exception: {e}\n{traceback.format_exc()}"
            # Now, 'exception_message' contains the formatted traceback message
            # You can use or save this variable as needed
            print(exception_message)  # This will print the full traceback information
            sendcustom_message('Second except statement' + exception_message)
            await asyncio.sleep(1)
